mapper.insert:
insert into board (
	boardnum,
	custid,
	title,
	content
	
	, originalfile
	, savedfile
	
)
values(
	board_seq.nextval,#{custid},#{title},#{content}
	
	,#{originalfile},#{savedfile}
	
)

mapper.update:
update board set 
	title=#{title},
	content=#{content}
	
	,originalfile=#{originalfile}
	,savedfile=#{savedfile}
	
	where boardnum = #{boardnum}

mapper.select:

	select *
	from (select * from board order by hits desc)
	where rownum <= 3
